import axios from "axios";
import React, { Component } from "react";
import "./Profile.css"
export default class Profile extends Component {
      constructor(props){
        super(props);
        this.state={
          
            users:{
              userName:"",
              followers:0,
              following:0,
              noOfPosts:0,
              profilePic:"",
            },
            posts:[]
        }
      }

      componentDidMount(){
        const userName = localStorage.getItem("userName");
        const password = localStorage.getItem("password");
        axios
          .get(`http://localhost:8084/user/profile/${userName}`, {
            headers: {
              authorization: "Basic " + window.btoa(userName + ":" + password),
            },
          })
          .then((res) => {
            this.setState({users:res.data});
          });

          axios
          .get(`http://localhost:8084/user/profilePost/${userName}`, {
            headers: {
              authorization: "Basic " + window.btoa(userName + ":" + password),
            },
          })
          .then((res) => {
            this.setState(console.log(res.data));
          });
      }

  render() {
    return (
      <div>
        <div>
          <div class="container">
            <div class="profile">
              <div class="profile-image">
                <img
                  src={this.state.users.profilePic} alt=""
                  style={{width:"100px",height:"100px"}}
                />
              </div>

              <div class="profile-user-settings">
                <h1 class="profile-user-name">{this.state.users.userName}</h1>

                <button class="btn profile-edit-btn">Edit Profile</button>

                <button
                  class="btn profile-settings-btn"
                  aria-label="profile settings"
                >
                  <i class="fas fa-cog" aria-hidden="true"></i>
                </button>
              </div>

              <div class="profile-stats">
                <ul>
                  <li>
                    <span class="profile-stat-count">{this.state.users.noOfPosts}</span> posts
                  </li>
                  <li>
                    <span class="profile-stat-count">{this.state.users.followers}</span> followers
                  </li>
                  <li>
                    <span class="profile-stat-count">{this.state.users.following}</span> following
                  </li>
                </ul>
              </div>

              <div class="profile-bio">
                <p>
                  <span class="profile-real-name">Jane Doe</span> Lorem ipsum
                  dolor sit, amet consectetur adipisicing elit 📷✈️🏕️
                </p>
              </div>
            </div>
          </div>
        </div>

        <div>
          {this.state.posts.map(post=>
          <div class="container">
            <div class="gallery">
              <div class="gallery-item" tabindex="0">
                <img
                  src="https://images.unsplash.com/photo-1511765224389-37f0e77cf0eb?w=500&h=500&fit=crop"
                  class="gallery-image"
                  alt=""
                />

                <div class="gallery-item-info">
                  <ul>
                    <li class="gallery-item-likes">
                      <span class="visually-hidden">Likes:</span>
                      <i class="fas fa-heart" aria-hidden="true"></i> 56
                    </li>
                    <li class="gallery-item-comments">
                      <span class="visually-hidden">Comments:</span>
                      <i class="fas fa-comment" aria-hidden="true"></i> 2
                    </li>
                  </ul>
                </div>
              </div>

            </div>
            {/* <!-- End of gallery --> */}

            <div class="loader"></div>
          </div>
          )}
        </div>
      </div>
    );
  }
}
