import React, { Component } from "react";
import { withRouter, Link } from "react-router-dom";
import { storage } from "./Firebase";
import { ref } from "@firebase/storage";
import { getDownloadURL } from "firebase/storage";
import { uploadBytesResumable } from "firebase/storage";
import axios from "axios";
class UpdateProfile extends Component {
  constructor(props){
    super(props);
    this.handlePost = this.handlePost.bind(this);
    this.onImageChange = this.onImageChange.bind(this);
    this.uploadFile = this.uploadFile.bind(this);
  }

  uploadFile() {
    let img = this.state.image;
    if (!img) return;
    const storageRef = ref(storage, `/image/${img.name}`);
    const uploadTask = uploadBytesResumable(storageRef, img);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const prog = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        this.setState({ progress: prog });
      },
      (err) => console.log(err),
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then(
          (url) => this.setState({ firebaseUrl: url }),
          console.log(this.state.firebaseUrl)
        );
      }
    );
  }
  handlePost(e) {
    const data = {
      pic: this.state.firebaseUrl,
    };
    const userName = localStorage.getItem("userName");
    const password = localStorage.getItem("password");
    console.log(userName);
    console.log(password);
    e.preventDefault();

    console.log(this.state.firebaseUrl);
    axios.post(`http://localhost:8084/user/posts/${userName}`, data, {
      headers: {
        authorization: "Basic " + window.btoa(userName + ":" + password),
      },
    });
    window.location.reload(false);
  }
  onImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      let img = event.target.files[0];
      this.setState({
        blobImage: URL.createObjectURL(img),
        image: img,
      });
    }
  };
  render() {
    return (
      <div>
        <br/>
      <div className="container">
        <form>
          <div class="form-outline mb-4">
            <br/>
            <div className="container">
            <Link to="/profile">Go to Profile</Link>
            <h1>Select Image</h1>
            <img
              src={this.state.blobImage}
              style={{ width: "30em", height: "30vh", borderRadius: "2px" }}
            />
            <br />
            <input
              type="file"
              name="myImage"
              accept=" image/*"
              onChange={this.onImageChange}
            />
            <h5>Uploaded {this.state.progress}%</h5>
            <button
              className="btn btn-primary"
              type="button"
              onClick={this.uploadFile}
            >
              Upload
            </button>
            &nbsp;&nbsp;
            <button
              className="btn btn-primary"
              type="onClick"
              onClick={this.handlePost}
            >
              Post
            </button>
            <br />
          </div>
        </div>
          <div class="form-outline mb-4">
            <input type="password" id="form1Example2" class="form-control" />
            <label class="form-label" for="form1Example2">
              Password
            </label>
          </div>

          <div class="row mb-4">
            <div class="col d-flex justify-content-center">
              <div class="form-check">
                <input
                  class="form-check-input"
                  type="checkbox"
                  value=""
                  id="form1Example3"
                  checked
                />
                <label class="form-check-label" for="form1Example3">
                  {" "}
                  Remember me{" "}
                </label>
              </div>
            </div>

            <div class="col">
              <a href="#!">Forgot password?</a>
            </div>
          </div>

          <button type="submit" class="btn btn-primary btn-block">
            Sign in
          </button>
        </form>
      </div>
      </div>
    );
  }
}
export default withRouter(UpdateProfile);
